create table companies (id bigint generated by default as identity, about varchar(255), headline varchar(255), industry varchar(255), logo_url varchar(255), name varchar(255), primary key (id));
create table educations (id bigint generated by default as identity, degree varchar(255), description varchar(255), graduate_date date, institution varchar(255), is_current boolean, specialisation varchar(255), start_date date, profile_id bigint not null, primary key (id));
create table employers (id bigint generated by default as identity, full_name varchar(255), password varchar(255), phone_number varchar(255), work_email varchar(255), company_id bigint, location_id bigint, primary key (id));
create table experiences (id bigint generated by default as identity, company varchar(255), date_finished date, date_started date, description varchar(255), position varchar(255), profile_id bigint not null, primary key (id));
create table job_roles (id bigint generated by default as identity, title varchar(255), primary key (id));
create table jobs (id bigint generated by default as identity, title varchar(255), company_id bigint, related_role_id bigint, primary key (id));
create table languages (id bigint generated by default as identity, level varchar(255), name varchar(255), profile_talent_id bigint, primary key (id));
create table locations (id bigint generated by default as identity, city varchar(255), country varchar(255), primary key (id));
create table profiles (preferred_salary double, talent_id bigint not null, primary key (talent_id));
create table skills (id bigint generated by default as identity, name varchar(255), primary key (id));
create table talent_skills (talent_id bigint not null, skill_id bigint not null, primary key (talent_id, skill_id));
create table talents (id bigint generated by default as identity, email varchar(255), full_name varchar(255), password varchar(255), location_id bigint, profile_talent_id bigint, primary key (id));
alter table educations add constraint fk_educations_profile_id foreign key (profile_id) references profiles;
alter table employers add constraint fk_employers_company_id foreign key (company_id) references companies;
alter table employers add constraint fk_employers_location_id foreign key (location_id) references locations;
alter table experiences add constraint fk_experience_profile_id foreign key (profile_id) references profiles;
alter table jobs add constraint fk_jobs_company_id foreign key (company_id) references companies;
alter table jobs add constraint fk_jobs_related_role_id foreign key (related_role_id) references job_roles;
alter table languages add constraint fk_languages_profile_talent_id foreign key (profile_talent_id) references profiles;
alter table profiles add constraint fk_profiles_talent_id foreign key (talent_id) references talents;
alter table talent_skills add constraint fk_talent_skills_skill_id foreign key (skill_id) references skills;
alter table talent_skills add constraint fk_tallent_skills_talent_id foreign key (talent_id) references profiles;
alter table talents add constraint fk_talents_location_id foreign key (location_id) references locations;
alter table talents add constraint talents_profile_talent_id foreign key (profile_talent_id) references profiles;